// let x = 0;
// let y = 0;
let z = 0
let dim = 80.0;
let num = 100;


let mySpeed = 0

function setup() {
	createCanvas(windowWidth, windowHeight)

	slider = createSlider(0, 10, 5);
	slider.position(100, 50);
	slider.style('width', '80px');

}


function draw() {
	background(0)
	let val = slider.value();

	let s = 'CHANGE THE SPEED OF THE TRAIN';
	fill(255);
	text(s, 10, 10, 70, 80); // Text wraps within text box

	mySpeed += slider.value() //sliderSpeed.value() instead of '.1'

	house(-width * 2 + mySpeed % width * 3, 0) // setting the x,y of translate

	wire(-width + mySpeed % width * 3, 0)


	strommastDown()

	strommastUp()

	wireFront(-width + mySpeed*1.5%width*4, 0)


	pfosten(-width*.6 + mySpeed*2%width*1.4, 0)

}


function strommastUp() {




	strokeWeight(20)
	line(width, height / 20, width, height);
	strokeWeight(1)
	stroke(255)
	strokeWeight(2);
	strokeCap(SQUARE);
	let m = windowWidth / 100;
	let n = windowHeight / 4;
	let o = windowWidth;
	line(m, n, m + o, n);

	stroke(255)
	strokeWeight(1.5);
	strokeCap(SQUARE);
	let m2 = windowWidth / 100;
	let n2 = windowHeight / 6;
	let o2 = windowWidth;
	line(m2, n2, m2 + o2, n2);

	stroke(255)
	strokeWeight(1);
	strokeCap(SQUARE);
	let m3 = windowWidth / 100;
	let n3 = windowHeight / 6.5;
	let o3 = windowWidth;
	line(m3, n3, m3 + o3, n3);


	stroke(255);
	strokeWeight(0.5);
	strokeCap(SQUARE);
	let p = windowWidth / 100;
	let q = windowHeight / 4.5;
	let r = windowWidth;
	line(p, q, p + r, q);


}


function strommastDown() {

	stroke(255);
	strokeWeight(4);
	strokeCap(SQUARE);
	let a = windowWidth / 100;
	let b = windowHeight / 1.2;
	let c = windowWidth;
	line(a, b, a + c, b);

	stroke(255);
	strokeWeight(2);
	strokeCap(SQUARE);
	let d = windowWidth / 100;
	let e = windowHeight / 1.179;
	let f = windowWidth;
	line(d, e, d + f, e);

	stroke(255);
	strokeWeight(6);
	strokeCap(SQUARE);
	let g = windowWidth / 100;
	let h = windowHeight / 1.14;
	let i = windowWidth;
	line(g, h, g + i, h);

	stroke(255);
	strokeWeight(2);
	strokeCap(SQUARE);
	let j = windowWidth / 100;
	let k = windowHeight / 1.08;
	let l = windowWidth;
	line(j, k, j + l, k);

	stroke(30);
	strokeWeight(3);
	strokeCap(SQUARE);
	let m = windowWidth / 100;
	let n = windowHeight / 1.3;
	let o = windowWidth;
	line(m, n, m + o, n);

	stroke(30);
	strokeWeight(5);
	strokeCap(SQUARE);
	let p = windowWidth / 100;
	let q = windowHeight / 3.5;
	let r = windowWidth;
	line(p, q, p + r, q);

}




function wire(x, y) {
	push()
	translate(x, y)
	fill(255)
	ellipse(width / 4, height / 8, 30)
	ellipse(width / 6, height / 8, 30)

	strokeWeight(5)
	line(width / 4, height / 8, width / 6, height / 8);
	strokeWeight(8)
	line(width / 4.9, height / 8, width / 4.9, height / 1.2);
	strokeWeight(1)
	line(10, 157, 300, 120);
	line(450, 120, 700, 157);


	pop()

}

function wireFront(x, y) {
	push()
	translate(x, y) // animate/move the function

	strokeWeight(20)
	line(width, height / 15, width, height);
	strokeWeight(1)

	stroke(255)
	strokeWeight(3)
	line(10, 250, width, 100)
	pop()
}

function pfosten(xoffset, yoffset) {
	push()
	translate(xoffset, yoffset) // animate/move the function

	for(let x = 0; x <= 70; x += 70) {
		for(let y = 0; y <= height; y += 70) {
			stroke(255)
			strokeWeight(8)
			noFill()
			//top, left, right
			triangle(400, y - 70, 400 - 70, y, 400, y);
			stroke(255)
			strokeWeight(8)
			strokeCap(SQUARE);
			line(320, 0, 320, height);
		}
	}
	pop()
}

function house(x, y) {
	push()
	translate(x, y) // animate/move the function

	fill(190)
	rect(100, 600, 150, 150)
	rect(280, 600, 150, 150)
	rect(460, 600, 150, 150)
	rect(640, 600, 150, 150)
	rect(820, 600, 150, 150)
	rect(1000, 600, 150, 150)
	rect(1180, 600, 150, 150)
	rect(1360, 600, 150, 150)
	rect(1540, 600, 150, 150)
	rect(1720, 600, 150, 150)

	rect(820, 420, 150, 150)
	rect(1000, 420, 150, 150)
	rect(1180, 420, 150, 150)
	rect(1360, 420, 150, 150)

	rect(280, 240, 150, 150)
	rect(460, 240, 150, 150)
	rect(640, 240, 150, 150)
	rect(820, 240, 150, 150)
	rect(1000, 240, 150, 150)

	rect(1000, 60, 150, 150)
	rect(1360, 60, 150, 150)
	pop()
}
