var d = 10;
var pressed = false;
let toggle1 = false;
let toggle2 = false;
let toggle3 = false;
let Button1, Button2, Button3, guiHolder
let flip = true;

function setup() {
	createCanvas(windowWidth, windowHeight);

	guiHolder = createElement('div')
	guiHolder.class('gui-holder')

	//Button1
	Button1 = createButton('tic tac toe')
		.mousePressed(() => toggle1 = true)
		.mouseReleased(() => toggle1 = false);
	Button1.parent(guiHolder)
	Button1.style('color', '255, 0, 0')
	Button1.style('font-family', '')
	//frameRate(100);

	//Button2
	Button2 = createButton('Flicker Party')
		.mousePressed(() => toggle2 = true)
		.mouseReleased(() => toggle2 = false);
	Button2.parent(guiHolder)
	Button2.style('color', '255, 0, 0')
	Button2.style('font-family', '')
	//frameRate(100);

	//Button3
	Button3 = createButton('Flash Dance')
		.mousePressed(() => toggle3 = true)
		.mouseReleased(() => toggle3 = false);
	Button3.parent(guiHolder)
	Button3.style('color', '255, 0, 0')
	Button3.style('font-family', '')
	//frameRate(100);

	createElement('style', `
	.gui-holder{
		position: fixed;
		right:5px;
		top: 5px;
	}
	`)


}

function draw() {

	background(0)
	noStroke()

	//begin of flip
	if(frameCount % 3 == 0) {
		flip = !flip
	}

	//horizontal to Vertical
	grid = 50

	//Default Background
	if(!toggle1 && !toggle2) {

		for(var i = 0; i < 50; i++) {

			fill(255)
			if(i % 2 == 0) {
				fill(0)
			}
			rect(0, i * grid, width, grid)
		}
	}

	//Begin of toggles
	for(var i = 0; i < 50; i++) {

		if(toggle1) {
			if(flip) {
				fill(0)
				if(i % 2 == 0) {
					fill(255)
				}
				rect(i * grid, 0, grid, height)
			} else {
				fill(255)
				if(i % 2 == 0) {
					fill(0)
				}
				rect(0, i * grid, width, grid)
			}
		} //end of toggle1

		if(toggle2) {
			if(flip) {
				fill(0)
				if(i % 2 == 0) {
					fill(random(255), random(255), random(255))
				}
				rect(i * grid, 0, grid, height)
			} else {
				fill(255)
				if(i % 2 == 0) {
					fill(0)
				}
				rect(0, i * grid, width, grid)
			}
		} //end of toggle2


		if(toggle3) {
			if(flip) {
				fill(0)
				if(i % 2 == 0) {
					fill(random(0, 300))
					rect(i * grid, 0, grid, height)
				}
			} else {
				fill(255)
				if(i % 2 == 0) {
					fill(0)
				}
				//rect(0, i * grid, width, grid)
			}
		} //end of toggle3

	} //end of loop


	//small box on top
	for(var i = 0; i < 10; i++) {

		fill(255)
		if(i % 2 == 0) {
			fill(0)
		}
		rect(10 * grid + i * grid, grid * 4, grid, grid * 8)
	}
}
