// frameCount

function lineBox(x, y, w, h, loopCount, sw) {
	push()
	strokeWeight(sw)
	for(let i = 0; i < loopCount; i++) {
		let xPos = map(i, 0, loopCount - 1, x, x + w)
		line(xPos, y, xPos, y + h)
	}
	pop()

}

function lineChaos(x, y, w, h, loopCount, sw) {
	push()
	strokeWeight(sw)
	for(let i = 0; i < loopCount; i++) {
		let xPos = map(i, 0, loopCount - 1, x, x + w)
		line(width / 4 * 1.92, random(y), width / 4 * 2.64, random(y) + random(w))
	}
	pop()
}




