function preload() {
	font = loadFont("data/FUTURAHI.TTF")
}

function setup() {
	createCanvas(windowWidth, windowHeight)

	sliderRauschen = createSlider(0, 1, 0, .001);
	sliderRauschen.position(width / 2, 10);
	sliderRauschen.style('width', '100px');

	checkboxNoise = createCheckbox('K4', false)
	checkboxNoise.position(width / 2, 50)
	checkboxNoise.style('color', 'white')


	// pass p5 canvas to hydra as s0 source
	layer = createGraphics(width, height)
	pg = createGraphics(hc.width, hc.height)

		s0.init({
		src: layer.drawingContext.canvas
	})

	background(0)
	layer.textFont(font)
	layer.textCursive

	layer.textAlign(CENTER, CENTER)
}

function draw() {

	pg.drawingContext.drawImage(hc, 0, 0, pg.width, pg.height) // update texture

	layer.clear()
	layer.fill(0, 200, 255)
	layer.stroke(0, 0, 0)
	layer.strokeWeight(2)
	layer.textSize(layer.width / 2)
	layer.textCursive

	slidaX = sliderRauschen.value();

	let offset = 0
	if(checkboxNoise.checked()) {
	layer.text('K4', layer.width / 2 + sin(frameCount * .03) * offset, layer.height / 2-83)
}}

