let font, words = ['«REACTIVE', 'RECODE»', 'COMING', 'SOON..!']

function preload(){
	font = loadFont('includes/fonts/RobotoMono-Regular.ttf')
}

function setup() {
	createCanvas(windowWidth, windowHeight)
	textFont(font)
	setupAudio();
}

function draw() {
	updateAudio();
	background(0, 15)
	fill(255)
	textSize(100-amp)
	textAlign(LEFT, CENTER)
	if(frameCount % 5 == 0) {
		text(words[floor(frameCount*.05)%words.length], frameCount*10%width, frameCount*15%height)
	}

}

/* AUDIO INIT */
let mic, fftRaw, fft = [],
	waveform = [],
	amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	userStartAudio();
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 500; // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 500; // average right amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
