// let checkboxBold

let sliderSpeed, rot = 0, rotSpecial = 0;
let sliderStroke, weight = 20

let flip = true
let flipSpeed = 20 // bigger = slower

function setup() {
	createCanvas(windowWidth, windowHeight)
	angleMode(DEGREES)

	sliderSpeed = createSlider(0, 100, 1, 1)
	//sliderSpeed = createSlider(5, 200, 50, 1)	// (min, max, start, Schritte) Drehgeschw. = rotSpeed?
	sliderSpeed.position(width/3, 20)

	sliderFlip = createSlider (0, 100, 50, 1)
	sliderFlip.position(width/2,20)

	sliderStroke = createSlider(0, 100, 50, 1)
	sliderStroke.position(width/6, 20)

	// checkboxBold = createCheckbox("bold", false)
	// checkboxBold.position(width/4, 20)
	// checkboxBold.style("color", "#f64f9e")
	// checkboxBold.style("font-family", "sans-serif")


}

function draw() {

	if(frameCount % flipSpeed == 0) { // flipSpeed should be slider.value()
		flip = !flip
	}




	translate(width / 2, height / 2)
	textSize(200)
	textAlign(CENTER, CENTER)

	if(flip){
		background(0)
		fill(255)
		stroke(255)
		rotSpecial = 0
	}else{
		background(255)
		fill(0)
		stroke(0)
		rotSpecial = 0 // extra rotation for one
	}


	//rotate(rotSpeed)
	//weight(sliderStroke.value + weight) // extra thickness
	rotate(rot + rotSpecial)
	textSize(200)
	//textStyle(NORMAL)
	textAlign(CENTER, CENTER)
	text('LUX', 0, 0)

	//weight += sliderStroke.value()
	rot += sliderSpeed.value()
	//change += flipSpeed.value

	//setTimeout(whiteBack, 1000);

	// setInterval(whiteBack, 2000);

	function whiteBack() {
		background(255)
	fill(0)
	translate(0, 0)
		//rotate(rotSpeed)
		//textStyle(NORMAL)
		text('LUX', 0, 0)

	 }

	//stopAnimation()

}
